import React, { useState, useEffect } from 'react';
import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://127.0.0.1:4000',
    timeout: 1000,
    withCredentials: true,
    credentials: 'include'
  });



function Notes() {
    const [notes, setNotes] = useState([]);

    const handleNewNote = (e) => {
        e.preventDefault();

        window.location='/newnote';
    }

    useEffect( () => {
        instance.post('/getnotes', { headers: { Cookie: '/refresh_cookie' } })
        .then( res => {
            console.log(res.data.notes);
            setNotes(res.data.notes);
            })
        .catch( err => {
            console.log('Error: ' + err);
        })
        }, []);


    return(
    <div>
        <button type='button' className='btn' onClick={handleNewNote} style={{border: '1px solid LightSkyBlue', color: 'black', backgroundColor: 'LightSkyBlue', 
                            padding: '8px', borderTopRightRadius: '20px', borderBottomRightRadius: '20px',
                            borderTopLeftRadius: '0px', borderBottomLeftRadius: '0px', borderColor: 'LightSkyBlue'}}>New Note +</button>
        <div>
            {notes.map( (note) => {
                return( 
                    <div  key={note._id} style={{display: 'flex', justifyContent: 'space-between', marginBottom: '20px'}}>
                    <div></div>
                    <div style={{width: '80%'}}>
                        <div style={{backgroundColor: 'aliceblue', borderRadius: '8px', borderBottomRightRadius: '0px', paddingLeft: '10px',
                            paddingTop: '5px', paddingBottom: '5px'}}>
                        <h4 >{note.title}</h4>
                        <div style={{whiteSpace: 'pre-wrap'}}>{note.body}</div>
                        </div>
                        <button className='btn' style={{border: '1px solid LightSkyBlue',  fontSize: '15px', color: 'black', backgroundColor: 'lightskyblue',
                            borderRadius: '20px', borderTopRightRadius: '0px', borderTopLeftRadius: '0px', paddingTop: '0px', paddingBottom: '0px',
                            justifyContent: 'right'}}>
                                Edit</button>
                    </div>
                    <div></div>
                    </div>
                );
            } ) }
        </div>
    </div>
    );
}
//{notes.map((e)=>{<Note title={e.title} body={e.body} updatedAt={e.updatedAt} />})}
export function Main() {
    const [element, setElement] = useState(<div>---</div>);

    useEffect( () => {
        instance.post('/protected', { headers: { Cookie: '/refresh_cookie' } })
        .then( res => { 
            console.log("user ID: " );
            console.log(res.data.id);
            if ( res.data.id == undefined ) window.location='/login';
            else setElement (<Notes/>);
            })
        .catch( err => {
            console.log('Error: ' + err);
            window.location='/login'; }
        )
    }, []);

    return(<div>{element}</div>);
}
