import React, { useState, useEffect } from 'react';
import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://127.0.0.1:4000',
    timeout: 1000,
    withCredentials: true,
    credentials: 'include'
  });

function Notes() {

    const handleNewNote = (e) => {
        e.preventDefault();

        window.location='/newnote';
    }

    useEffect( () => {
        instance.post('/getnotes', { headers: { Cookie: '/refresh_cookie' } })
        .then( res => {
            console.log(res.data.notes);
            })
        .catch( err => {
            console.log('Error: ' + err);
        })
        }, []);


    return(
    <div>
        <button type='button' className='btn' onClick={handleNewNote}>New Note +</button>
        <div>
            {notes}
        </div>
    </div>
    );
}

export function Main() {
    const [element, setElement] = useState(<div>---</div>);

    useEffect( () => {
        instance.post('/protected', { headers: { Cookie: '/refresh_cookie' } })
        .then( res => { 
            console.log("user ID: " );
            console.log(res.data.id);
            if ( res.data.id == undefined ) window.location='/login';
            else setElement (<Notes/>);
            })
        .catch( err => {
            console.log('Error: ' + err);
            window.location='/login'; }
        )
    }, []);

    return(<div>{element}</div>);
}
