import React, { useState, useEffect } from 'react';
import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://127.0.0.1:4000',
    timeout: 1000,
    withCredentials: true,
    credentials: 'include'
  });

export function NewNote() {
    const [data, setData] = useState({
        userId: document.cookie.substring(document.cookie.indexOf('=')+1),
        title: "",
        body: ""
    });

    const handleSubmit = (e) => {
        e.preventDefault();

        console.log(data);

        instance.post('https://127.0.0.1:4000/newnote', data)
            .then(res => { 
                console.log(data);
                if ( res.error != undefined ) window.location='/login';
                else window.location='/';
            } )
            .catch( err => {throw new Error('Error: ' + err )});
    }

    return(
        <>
        <div className='form' style={{marginLeft: '50px', marginRight: '50px'}} onSubmit={handleSubmit}>
            <div className='form-group'>
                <input type='text' className='form-control' value={data.title} placeholder='New Note'  style={{marginBottom: '5px'}}
                onChange={(e)=>{setData({...data, title: e.target.value})}} />
            </div>
            <div className='form-group'>
                <textarea type='text' cols="40" rows="5" className='form-control' value={data.body} placeholder='Write new note!'
                style={{resize: 'none', height: '300px', marginBottom: '5px'}}
                onChange={(e)=>{setData({...data, body: e.target.value})}} />
            </div>
            <button type='submit' className='btn' style={{backgroundColor: 'LightSkyBlue'}}>Add Note</button>
        </div>
        </>
    );
}
